bottom = 'bottom'
top = 'top'
left = 'left'
right = 'right'

ANCHOR_CENTER = 'center'#中
ANCHOR_N = 'n'#上
ANCHOR_NW = 'nw'#左上
ANCHOR_NS = 'ne'#右上
ANCHOR_S = 's'#下
ANCHOR_SW = 's'#左下
