def setTitleIco(window, title, ico_code:str = None):
    window.title(title)
    if ico_code and isfile(ico_code):
        window.iconbitmap(ico_code)
    else:
        if not ico_code or len(ico_code) == 0:
            ico_code = base64.b64decode(SYSTEMUI_ICO)
        workThread.LockUtil.acquire()
        ico = abspath('tmp.ico')
        tmp = open(ico, "wb+")
        tmp.write(ico_code)
        tmp.close()
        if isfile(ico):
            window.iconbitmap(ico)
            os.remove(ico)
        workThread.LockUtil.release()

class GressBar():
    def __init__(self):
        self.tip = ''
        self.isLoop = False

    def start(self, title='下载Jira', lableTxt='任务正在运行中,请稍等……', ico = None):
        height = 150
        width = 300
        self.master = Toplevel()
        self.master.protocol("WM_DELETE_WINDOW", lambda :print('休想关闭'))
        setTitleIco(self.master, title, ico)
        self.master.attributes('-topmost',True)
        top = self.master
        overrideredirect = False
        top.overrideredirect(overrideredirect)
        top.title(title)
        Label(top, text=lableTxt, fg="green").pack(pady=5)
        prog = ttk.Progressbar(top, mode='indeterminate', length=200)
        prog.pack(pady=13, padx=35)
        prog.start()
        self.tipLable = tk.Label(self.master, text=self.tip, fg="green")
        self.tipLable.pack(pady=14)
        top.resizable(False, False)
        top.update()
        screenwidth = self.master.winfo_screenwidth()
        screenheight = self.master.winfo_screenheight()
        alignstr = '%dx%d+%d+%d' % (width, height, (screenwidth - width) / 2, (screenheight - height) / 2)
        top.geometry(alignstr)
        self.isLoop = True
        self.start = time.clock()
        logUtils.debug('任务开始')
        top.mainloop()

    def updateMsg(self, msg:str):
        self.tip = msg
        if self.isLoop and hasattr(self, 'tipLable'):
            self.tipLable.config(text=msg)

    def quit(self):
        if self.isLoop:
            self.end = time.clock()
            logUtils.debug('任务结束 --- 使用时间({})'.format(toolUtils.getUsedTimeStr(self.start, self.end)))
            self.master.destroy()
            self.master = None
